<?phpif (!isset($_COOKIE['user_id']) && !isset($_COOKIE['password'])){	header("Location: index.php");}else{	$cook1 = clean($_COOKIE['user_id']);	$cook2 = clean($_COOKIE['password']);	$getinfo = mysql_fetch_object(mysql_query("SELECT id,password,online,username,look,vip_points,credits,activity_points,colorpick FROM users WHERE id = '".$cook1."'"));	if ($getinfo->password != $cook2)	{		header("Location: index.php");	}}?>